// src/components/TransactionTable.jsx
import React, { useState, useEffect } from 'react';
import './TransactionTable.css';
import transactionsData from '../assets/data/transactions'; // Importa los datos
import { Pagination } from '@ark-ui/react/pagination';

function TransactionTable() {
  const [transactions, setTransactions] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const pageSize = 10; // Número de transacciones por página
  const totalTransactions = transactionsData.length; // Número total de transacciones
  const totalPages = Math.ceil(totalTransactions / pageSize); // Número total de páginas

  useEffect(() => {
    // Simula una solicitud Axios con paginación
    const fetchData = async () => {
      // Simula un retraso en la solicitud
      await new Promise(resolve => setTimeout(resolve, 500)); // 500ms de retraso

      // Calcula el índice de inicio y fin para la paginación
      const startIndex = (currentPage - 1) * pageSize;
      const endIndex = startIndex + pageSize;

      // Obtiene las transacciones para la página actual
      const paginatedTransactions = transactionsData.slice(startIndex, endIndex);

      // Simula la respuesta de Axios
      const response = {
        data: paginatedTransactions, // Utiliza los datos paginados
      };

      setTransactions(response.data); // Actualiza el estado con los datos
    };

    fetchData(); // Llama a la función fetchData al montar el componente o al cambiar la página
  }, [currentPage]); // El array vacío asegura que el efecto se ejecute solo una vez (como componentDidMount)

  // Función para cambiar la página
  const handlePageChange = (newPage) => {
    setCurrentPage(newPage);
  };

  return (
    <div className="ark-table-container">
      <table className="ark-table">
        <caption>
          Customer Transactions
        </caption>
        <thead>
          <tr>
            <th className="ark-table-header">ID</th>
            <th className="ark-table-header">Customer Name</th>
            <th className="ark-table-header">Revenue Source</th>
            <th className="ark-table-header">Currency Code</th>
            <th className="ark-table-header">Transaction Date</th>
          </tr>
        </thead>
        <tbody>
          {transactions.map(transaction => (
            <tr key={transaction.id} className="ark-table-row">
              <td className="ark-table-cell">{transaction.id}</td>
              <td className="ark-table-cell">{transaction.customername}</td>
              <td className="ark-table-cell">{transaction.revenuesourcename}</td>
              <td className="ark-table-cell">{transaction.currencycode}</td>
              <td className="ark-table-cell">{transaction.transactiondate}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <div className="pagination-container">
          <Pagination.Root count={totalTransactions} pageSize={pageSize} onChange={handlePageChange}>
            <Pagination.PrevTrigger isDisabled={currentPage === 1}>Previous</Pagination.PrevTrigger>
            <Pagination.NextTrigger isDisabled={currentPage === totalPages}>Next</Pagination.NextTrigger>
          </Pagination.Root>
      </div>
    </div>
  );
}

export default TransactionTable;
