// src/components/AvailableBalances.jsx
import React, { useState, useEffect } from 'react';
import './AvailableBalances.css';
import availableBalances from '../assets/data/availableBalances'; // Importa los datos

function AvailableBalances() {
  const [balances, setBalances] = useState([]);

  useEffect(() => {
    // Simula una solicitud Axios
    const fetchData = async () => {
      // Simula un retraso en la solicitud
      await new Promise(resolve => setTimeout(resolve, 500));

      // Simula la respuesta de Axios
      const response = {
        data: availableBalances, // Utiliza los datos importados
      };

      setBalances(response.data); // Actualiza el estado con los datos
    };

    fetchData(); // Llama a la función fetchData al montar el componente
  }, []);

  return (
    <div className="available-balances-container">
      <h2>Available Balances</h2>
      <div className="balances-grid">
        {balances.map(balance => (
          <div key={`${balance.revenue_source_name}-${balance.currency_code}`} className="balance-card">
            <h3>{balance.revenue_source_name}</h3>
            <p>Currency: {balance.currency_code}</p>
            <p className="amount">Available Amount: {balance.availableamount}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default AvailableBalances;
