import React, { useEffect } from 'react';
import Header from './components/Header';
import TransactionTable from './components/TransactionTable';
import AvailableBalances from './components/AvailableBalances';
import RevenueSources from './components/RevenueSources';
import { Tabs } from '@ark-ui/react/tabs';
import './App.css';
import gsap from 'gsap';

function App() {
    const handleTabChange = (value) => {
        // Animate the content transition
        const contents = document.querySelectorAll('.tabs-content');
        contents.forEach(content => {
            if (content.getAttribute('data-value') === value) {
                gsap.to(content, { opacity: 1, duration: 0.6, display: 'block' });
            } else {
                gsap.to(content, { opacity: 0, duration: 0.6, display: 'none' });
            }
        });
    };

    useEffect(() => {
      // Set the initial state to display only the first tab
      const initialTab = document.querySelector(`.tabs-content[data-value="transactions"]`);
      if (initialTab) {
          gsap.set(initialTab, { opacity: 1, display: 'block' });
      }
    }, []);

    return (
        <div className="App">
            <Header />
            <div className="tabs-container">
                <Tabs.Root defaultValue="transactions" onChange={(details) => handleTabChange(details.value)}>
                    <Tabs.List className="tabs-list">
                        <Tabs.Trigger className="tabs-trigger" value="transactions">
                            Transacciones
                        </Tabs.Trigger>
                        <Tabs.Trigger className="tabs-trigger" value="balances">
                            Saldos Disponibles
                        </Tabs.Trigger>
                        <Tabs.Trigger className="tabs-trigger" value="revenueSources">
                            Fuentes de Ingresos
                        </Tabs.Trigger>
                    </Tabs.List>

                    <div className="tabs-content-wrapper">
                        <Tabs.Content data-value="transactions" className="tabs-content">
                            <TransactionTable />
                        </Tabs.Content>
                        <Tabs.Content data-value="balances" className="tabs-content">
                            <AvailableBalances />
                        </Tabs.Content>
                        <Tabs.Content data-value="revenueSources" className="tabs-content">
                            <RevenueSources />
                        </Tabs.Content>
                    </div>
                </Tabs.Root>
            </div>
        </div>
    );
}

export default App;
