// src/components/TransactionTable.jsx
import React, { useState, useEffect } from 'react';
import './TransactionTable.css';
import transactionsData from '../assets/data/transactions';
import { Pagination } from '@ark-ui/react/pagination';

function TransactionTable() {
  const [transactions, setTransactions] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const pageSize = 10;
  const totalTransactions = transactionsData.length;

  useEffect(() => {
    const fetchData = async () => {
      await new Promise(resolve => setTimeout(resolve, 500));
      const startIndex = (currentPage - 1) * pageSize;
      const endIndex = startIndex + pageSize;
      const paginatedTransactions = transactionsData.slice(startIndex, endIndex);
      setTransactions(paginatedTransactions);
    };

    fetchData();
  }, [currentPage]);

  const handlePageChange = (details) => {
    setCurrentPage(details.page);
  };

  return (
    <div className="ark-table-container">
      <div className="pagination-container">
        <Pagination.Root 
          count={totalTransactions} 
          pageSize={pageSize} 
          siblingCount={1}
          onPageChange={handlePageChange}
        >
          <Pagination.PrevTrigger>Anterior</Pagination.PrevTrigger>
          <Pagination.Content>
            {(pages) =>
              pages.map((page, index) => {
                if (page.type === 'page') {
                  return (
                    <Pagination.Item key={index} {...page}>
                      {page.value}
                    </Pagination.Item>
                  );
                }
                return <Pagination.Ellipsis key={index}>...</Pagination.Ellipsis>;
              })
            }
          </Pagination.Content>
          <Pagination.NextTrigger>Siguiente</Pagination.NextTrigger>
        </Pagination.Root>
      </div>

      <table className="ark-table">
        <caption>Customer Transactions</caption>
        <thead>
          <tr>
            <th className="ark-table-header">ID</th>
            <th className="ark-table-header">Customer Name</th>
            <th className="ark-table-header">Revenue Source</th>
            <th className="ark-table-header">Currency Code</th>
            <th className="ark-table-header">Transaction Date</th>
          </tr>
        </thead>
        <tbody>
          {transactions.map(transaction => (
            <tr key={transaction.id} className="ark-table-row">
              <td className="ark-table-cell">{transaction.id}</td>
              <td className="ark-table-cell">{transaction.customername}</td>
              <td className="ark-table-cell">{transaction.revenuesourcename}</td>
              <td className="ark-table-cell">{transaction.currencycode}</td>
              <td className="ark-table-cell">{transaction.transactiondate}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default TransactionTable;
