import React, { useRef, useEffect } from 'react';
import Header from './components/Header';
import TransactionTable from './components/TransactionTable';
import AvailableBalances from './components/AvailableBalances';
import RevenueSources from './components/RevenueSources';
import { Tabs } from '@ark-ui/react/tabs';
import './App.css';
import gsap from 'gsap';

function App() {
    const contentRefs = useRef({});

    useEffect(() => {
        // Initialize refs for each content
        contentRefs.current = {
            transactions: useRef(null),
            balances: useRef(null),
            revenueSources: useRef(null),
        };

          // Set the initial state to display only the first tab
        Object.keys(contentRefs.current).forEach(key => {
            const el = contentRefs.current[key].current;
            if (el) {
                if (key === "transactions") {
                    gsap.set(el, { opacity: 1, display: 'block' });
                } else {
                    gsap.set(el, { opacity: 0, display: 'none' });
                }
            }
        });
    }, []);

    const handleTabChange = (value) => {
        // Animate the content transition
        Object.keys(contentRefs.current).forEach(key => {
            const el = contentRefs.current[key].current;
            if (el) {
                if (key === value) {
                    gsap.to(el, { opacity: 1, duration: 0.6, display: 'block' });
                } else {
                    gsap.to(el, { opacity: 0, duration: 0.6, display: 'none' });
                }
            }
        });
    };

    return (
        <div className="App">
            <Header />
            <div className="tabs-container">
                <Tabs.Root defaultValue="transactions" onChange={(details) => handleTabChange(details.value)}>
                    <Tabs.List className="tabs-list">
                        <Tabs.Trigger className="tabs-trigger" value="transactions">
                            Transacciones
                        </Tabs.Trigger>
                        <Tabs.Trigger className="tabs-trigger" value="balances">
                            Saldos Disponibles
                        </Tabs.Trigger>
                        <Tabs.Trigger className="tabs-trigger" value="revenueSources">
                            Fuentes de Ingresos
                        </Tabs.Trigger>
                    </Tabs.List>

                    <div className="tabs-content-wrapper">
                        <Tabs.Content ref={contentRefs.current.transactions} className="tabs-content" value="transactions">
                            <TransactionTable />
                        </Tabs.Content>
                        <Tabs.Content ref={contentRefs.current.balances} className="tabs-content" value="balances">
                            <AvailableBalances />
                        </Tabs.Content>
                        <Tabs.Content ref={contentRefs.current.revenueSources} className="tabs-content" value="revenueSources">
                            <RevenueSources />
                        </Tabs.Content>
                    </div>
                </Tabs.Root>
            </div>
        </div>
    );
}

export default App;
