import jsPDF from 'jspdf';
import 'jspdf-autotable';

export const generatePDF = (reportData) => {
  const doc = new jsPDF();

  // Add document title
  doc.setFontSize(18);
  doc.text(`Revenue Source Report: ${reportData.revenue_source_name}`, 14, 22);

  // Add customer name
  doc.setFontSize(12);
  doc.text(`Customer: ${reportData.customer_name}`, 14, 30);

  // Prepare data for the table
  const tableColumn = ["Month", "Currency", "Amount"];
  const tableRows = [];

  reportData.month_info.forEach(monthData => {
    tableRows.push([
      monthData.month,
      monthData.currency_code,
      monthData.sum
    ]);
  });

  // Add the table to the PDF
  doc.autoTable({
    head: [tableColumn],
    body: tableRows,
    startY: 40, // Start Y position after the title
  });

  // Save the PDF
  doc.save(`report_${reportData.revenue_source_name}.pdf`);
};
