import React, { useState, useEffect } from 'react';
import './RevenueSources.css';
import revenueSourcesData from '../assets/data/revenueSources';
import availableBalancesData from '../assets/data/availableBalances';
import pdfReports from '../assets/data/pdfReports';
import { generateMultiplePDFs } from '../utils/pdfGenerator';
import pdfIcon from '../assets/images/icons/pdf.svg';
import searchIcon from '../assets/images/icons/search.svg';
import filterIcon from '../assets/images/icons/filters.svg';
import { Slider, SliderTrack, SliderRange, SliderThumb } from '@ark-ui/react';

function CombinedComponent() {
    const [revenueSources, setRevenueSources] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [selectedSources, setSelectedSources] = useState([]);
    const [showFilter, setShowFilter] = useState(false);
    const [amountFilter, setAmountFilter] = useState([0, 0]);
    const [maxAmount, setMaxAmount] = useState(0); // Inicializado a 0 para evitar problemas iniciales
    const [minAmount, setMinAmount] = useState(0); // Inicializado a 0 para evitar problemas iniciales
    const [isDataLoaded, setIsDataLoaded] = useState(false); // Nuevo estado para controlar la carga de datos
    const [amountFilterValue, setAmountFilterValue] = useState(0); // Nuevo estado para controlar la carga de datos

    useEffect(() => {
        const fetchData = async () => {
            const sortedData = [...revenueSourcesData].sort((a, b) =>
                a.revenue_source_name.localeCompare(b.revenue_source_name)
            );
            setRevenueSources(sortedData);

            // Calculate the maximum and minimum available amount
            const amounts = availableBalancesData.map(bal => bal.availableamount);
            const calculatedMaxAmount = Math.max(...amounts, 0);
            const calculatedMinAmount = Math.min(...amounts, 0);

            setMaxAmount(calculatedMaxAmount);
            setMinAmount(calculatedMinAmount);

            setIsDataLoaded(true); // Marcar los datos como cargados
            setAmountFilter([0, calculatedMaxAmount]);
            setAmountFilterValue(calculatedMaxAmount)
        };

        fetchData();
    }, []);


    const handleSearch = (event) => {
        setSearchTerm(event.target.value);
    };

    const filteredSources = revenueSources.filter(source =>
        source.revenue_source_name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    const handleCardClick = (sourceId) => {
        setSelectedSources(prev =>
            prev.includes(sourceId)
                ? prev.filter(id => id !== sourceId)
                : [...prev, sourceId]
        );
    };

    const handleDownloadSelected = () => {
        const selectedReports = selectedSources.map(sourceId => {
            const source = revenueSources.find(s => s.revenue_source_id === sourceId);
            return pdfReports.find(report => report.revenue_source_name === source.revenue_source_name);
        });
        generateMultiplePDFs(selectedReports);
    };

    const isDownloadDisabled = selectedSources.length === 0;

    const getCurrencySymbol = (currencyCode) => {
        switch (currencyCode) {
            case 'EUR': return '€';
            case 'GBP': return '£';
            case 'USD': return '$';
            default: return currencyCode;
        }
    };

    const toggleFilter = () => {
        setShowFilter(!showFilter);
    };

    // Slider code
    const handleAmountFilterChange = (value) => {
        setAmountFilterValue(value.value[0])
    };

    const filterByAmount = (balance) => {
        return balance.availableamount >= 0 && balance.availableamount <= amountFilterValue;
    };

    return (
        <div className="combined-component-container">
            <div className="combined-component-header">
                <h2>Revenue Sources and Available Balances</h2>
                <div className="header-actions">
                    <div className="search-container">
                        <img src={searchIcon} alt="Search Icon" className="search-icon" />
                        <input
                            type="text"
                            placeholder="Search revenue sources..."
                            value={searchTerm}
                            onChange={handleSearch}
                            className="combined-component-search"
                        />
                    </div>
                    <div className="download-and-filter">
                        <div className="tooltip-container">
                            <button
                                onClick={handleDownloadSelected}
                                className="download-selected-btn"
                                disabled={isDownloadDisabled}
                            >
                                <img src={pdfIcon} alt="PDF Icon" className="pdf-icon" />
                                Download Selected Reports
                                {selectedSources.length > 0 && (
                                    <span className="selected-count">{selectedSources.length}</span>
                                )}
                            </button>
                            <div className="tooltip">
                                Please select at least one Revenue Source to download.
                            </div>
                        </div>
                        <div className="filter-wrapper">
                            <button className="filter-button" onClick={toggleFilter}>
                                <img src={filterIcon} alt="Filter Icon" className="filter-icon" />
                            </button>
                            {showFilter && isDataLoaded && (
                                <div className="filter-container">
                                    <div className="filter-card">
                                        <h3>Filter by Available Amount</h3>
                                        <div className="slider-container">
                                            <span className="slider-min-value">{minAmount}</span>
                                            <Slider.Root
                                                defaultValue={[0, maxAmount]}
                                                min={minAmount}
                                                max={maxAmount}
                                                onValueChangeEnd={handleAmountFilterChange}
                                                aria-label="Volume"
                                            >
                                                <Slider.Control>
                                                    <Slider.Track>
                                                        <Slider.Range />
                                                    </Slider.Track>
                                                    <Slider.Thumb index={0}>
                                                        <div className="slider-thumb-value">{amountFilterValue}</div>
                                                        <Slider.HiddenInput />
                                                    </Slider.Thumb>
                                                </Slider.Control>
                                            </Slider.Root>
                                            <span className="slider-max-value">{maxAmount}</span>
                                        </div>
                                        <div>
                                            <p>Current selected value: {amountFilterValue}</p>
                                        </div>
                                    </div>
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            </div>
            <div className="combined-component-grid">
                {filteredSources.map(source => {
                    const balance = availableBalancesData.find(bal => bal.revenue_source_name === source.revenue_source_name);
                    const hasBalance = balance && filterByAmount(balance);
                    const isSelected = selectedSources.includes(source.revenue_source_id);

                    return (
                        <div
                            key={source.revenue_source_id}
                            className={`combined-component-card ${isSelected ? 'selected' : ''} ${hasBalance ? '' : 'no-balance'}`}
                            onClick={() => handleCardClick(source.revenue_source_id)}
                        >
                            {isSelected && <img src={pdfIcon} alt="PDF Icon" className="card-pdf-icon" />}
                            <h3>{source.revenue_source_name}</h3>
                            {balance ? (
                                <>
                                    <p className="available-amount-label">Available Amount</p>
                                    <p className="available-amount-value">{getCurrencySymbol(balance.currency_code)}{balance.availableamount}</p>
                                </>
                            ) : (
                                <p>No available amount</p>
                            )}
                        </div>
                    );
                })}
            </div>
        </div>
    );
}

export default CombinedComponent;
